import React, { Component } from "react";
import "../index.css";
import firebase from "./firebase.js";


class Search extends Component {
  constructor(props) {
    super(props);
    this.state = {
      location: ""
    };
  };
  handleInputChange = event => {
    let value = event.target.value;
    const name = event.target.name;

    this.setState({
      [name]: value
    });
  };
  componentDidMount() {
    const listingsRef = firebase.database().ref("listings");
    listingsRef.on("value", (snapshot) => {
      let listings = snapshot.val();
      let newState = [];
      for (let name in listings) {
        newState.push({
          id: name,
          name: listings[name].name,
          location: listings[name].location,
          email: listings[name].email,
          price: listings[name].price,
          phone: listings[name].phone,
          availability: listings[name].availability,
          user: listings[name].user
        });
      }
      let filteredState = newState.filter(user => {
        return user = this.state.currentUser;
      });
      this.setState({
        name: newState
      });
      console.log(newState);
    });
  };
  render() {
    const locationState = this.state.location;

    let search
    if (locationState) {
      search = <a onClick={this.searchAll} id="searchButton">Search</a>
    } else {
      search = <a id="noSearch">Search</a>
    }
    return (
      <div className="searchPage">
        <div className="row">
          <div className="col s12 m6">
            <h4>Find Your Ride</h4>
            <a href="/"><i className="fas fa-arrow-left"></i>&nbsp;Back</a>
            <form>
              <div class="row">
                <div className="row">
                  <h5>Search By Bike Station</h5>
                <div class="input-field col s3">
                  <label>
                    <input
                      name="location"
                      type="radio"
                      value="Georgia Tech"
                      onChange={this.handleInputChange}
                      />
                    <span>Georgia Tech</span>
                  </label>
                </div>
                <div class="input-field col s3">
                  <label>
                    <input
                      name="location"
                      type="radio"
                      value="Atlantic Station"
                      onChange={this.handleInputChange}
                      />
                    <span>Atlantic Station</span>
                  </label>
                </div>
                <div class="input-field col s3">
                  <label>
                    <input
                      name="location"
                      type="radio"
                      value="Midtown"
                      onChange={this.handleInputChange}
                      />
                    <span>Midtown</span>
                  </label>
                </div>
                <div class="input-field col s3">
                  <label>
                    <input
                      name="location"
                      type="radio"
                      value="Five Points"
                      onChange={this.handleInputChange}
                      />
                    <span>Five Points</span>
                  </label>
                </div>
              </div>
              <div className="row">
                <div class="input-field col s3">
                  <label>
                    <input
                      name="location"
                      type="radio"
                      value="Inman Park"
                      onChange={this.handleInputChange}
                      />
                    <span>Inman Park</span>
                  </label>
                </div>
                <div class="input-field col s3">
                  <label>
                    <input
                      name="location"
                      type="radio"
                      value="South Atlanta"
                      onChange={this.handleInputChange}
                      />
                    <span>South Atlanta</span>
                  </label>
                </div>
                <div class="input-field col s3">
                  <label>
                    <input
                      name="location"
                      type="radio"
                      value="Virginia Highland"
                      onChange={this.handleInputChange}
                      />
                    <span>Virginia Highland</span>
                  </label>
                </div>
                <div class="input-field col s3">
                  <label>
                    <input
                      name="location"
                      type="radio"
                      value="Buckhead"
                      onChange={this.handleInputChange}
                      />
                    <span>Buckhead</span>
                  </label>
                </div>
              </div>
              </div>
              {search}
            </form>
          </div>
          <div className="col s12 m6">
            <h4>Available Bikes</h4>
              {this.state.name.map(name => (
                  <div className="lenderListingContainer">
                    <div className="row">
                      <div className="col s12 m12">
                    <p>Username: {name.user}</p>
                    <p>Station: {name.location}</p>
                    <p>Name: {name.name}</p>
                    <p>Hourly Rate: {name.price}</p>
                    <p>Email: {name.email}</p>
                    <p>Phone: {name.phone}</p>
                    <p>Availability: {name.availability}</p>
                    <p>ID: {name.id}</p>
                    </div>
                  </div>
                </div>
                ))}
          </div>
        </div>
      </div>
    )
  };
};

export default Search;

