    return (
      <div className="lenderPage">
        <div className="row" id="lenderPageTitle">
          <h4>Welcome {this.state.currentUser}!</h4>
          <p>Here you can list a bike for rent or manage your existing bikes</p>
        </div>
      <div className="row">
        <div className="col s12 m6">
          {createPage}
        </div>
        <div className="col s12 m6">
          <h4>Current Bikes</h4>

        </div>
      </div>
    </div>
    );
  } else {
    return (
      <div className="login">
        <div className="row" id="loginPageTitle">
            <a href="/"><i className="fas fa-arrow-left"></i>&nbsp;Back</a>
        </div>
        <div className="row">
          <div className="col s12 m6">
      <h5>New User? Create an Account Below</h5>
        <div class="input-field col s12">
          <input
            placeholder="Please enter a username"
            name="newUser"
            id="newUser"
            type="text"
            class="validate"
            value={this.state.newUser}
            onChange={this.handleInputChange}
            />
        </div>
        <div class="input-field col s12">
          <input
            placeholder="Please create a password"
            name="newpassword"
            id="newpassword"
            type="password"
            class="validate"
            value={this.state.newpassword}
            onChange={this.handleInputChange}
            />
        </div>
          {create}
      </div>
      <div className="col s12 m6">
        <h5>Returning User? Log in below</h5>
          <div class="input-field col s12">
            <input
              placeholder="Please enter your username"
              name="currentUser"
              id="currentUser"
              type="text"
              class="validate"
              value={this.state.currentUser}
              onChange={this.handleInputChange}
              />
          </div>
          <div class="input-field col s12">
            <input
              placeholder="Please enter your password"
              name="password"
              id="password"
              type="password"
              class="validate"
              value={this.state.password}
              onChange={this.handleInputChange}
              />
          </div>
          {login}
      </div>
      </div>
    </div>
    );
  };
  };
};

